import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import Logo from './Logo';

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  const isActive = (path) => {
    return location.pathname === path ? 'nav-button active' : 'nav-button';
  };

  const navItems = [
    { label: 'Dashboard', path: '/dashboard', icon: 'üìä' },
    { label: 'Agents', path: '/agents', icon: 'üë•' },
    { label: 'Upload', path: '/upload', icon: 'üìÅ' },
    { label: 'Lists', path: '/distributed', icon: 'üìã' },
  ];

  return (
    <nav className="navbar">
      <div className="nav-content">
        <div onClick={() => navigate('/dashboard')} style={{ cursor: 'pointer' }}>
          <Logo size="md" showText={false} />
        </div>
        
        <div className="nav-links">
          {navItems.map((item) => (
            <button
              key={item.path}
              className={isActive(item.path)}
              onClick={() => navigate(item.path)}
            >
              {item.icon} {item.label}
            </button>
          ))}
          <button 
            onClick={handleLogout} 
            className="nav-button" 
            style={{ background: 'rgba(229, 62, 62, 0.2)', borderColor: 'rgba(229, 62, 62, 0.3)' }}
          >
            üö™ Logout
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
