import React, { useState, useEffect } from 'react';
import API from '../services/api';

const AgentList = () => {
  const [agents, setAgents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchAgents();
  }, []);

  const fetchAgents = async () => {
    try {
      const res = await API.get('/api/agents');
      setAgents(res.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch agents');
    }
    setLoading(false);
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div className="card" style={{ marginTop: '20px' }}>
      <h2>Agents List</h2>
      {error && <div className="alert alert-error">{error}</div>}
      
      {agents.length === 0 ? (
        <p style={styles.noData}>No agents found. Create your first agent above.</p>
      ) : (
        <div style={styles.tableContainer}>
          <table className="table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Mobile</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {agents.map(agent => (
                <tr key={agent._id}>
                  <td>
                    <div style={styles.agentInfo}>
                      <span style={styles.avatar}>ðŸ‘¤</span>
                      {agent.name}
                    </div>
                  </td>
                  <td>{agent.email}</td>
                  <td>{agent.mobile}</td>
                  <td>
                    <span style={styles.status}>Active</span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

const styles = {
  tableContainer: {
    overflowX: 'auto'
  },
  noData: {
    textAlign: 'center',
    padding: '40px',
    color: '#7f8c8d'
  },
  agentInfo: {
    display: 'flex',
    alignItems: 'center',
    gap: '10px'
  },
  avatar: {
    fontSize: '16px'
  },
  status: {
    padding: '4px 8px',
    backgroundColor: '#d4edda',
    color: '#155724',
    borderRadius: '12px',
    fontSize: '12px'
  }
};

export default AgentList;
