import React, { useState } from 'react';
import API from '../services/api';

const UploadList = () => {
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const onFileChange = e => {
    setFile(e.target.files[0]);
  };

  const onSubmit = async e => {
    e.preventDefault();
    if (!file) {
      setError('Please select a file');
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await API.post('/api/lists/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setMessage(res.data.message);
      setError('');
      setFile(null);
      document.getElementById('file-input').value = '';
    } catch (err) {
      setError(err.response?.data?.message || 'Error uploading file');
      setMessage('');
    }
    setLoading(false);
  };

  return (
    <div style={{ maxWidth: '500px', margin: '20px auto', padding: '20px' }}>
      <h2>Upload CSV File</h2>
      <p>File should contain columns: FirstName, Phone, Notes</p>
      <p>Accepted formats: CSV, XLSX, XLS</p>
      
      {message && <div style={{ color: 'green', marginBottom: '10px' }}>{message}</div>}
      {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>}
      
      <form onSubmit={onSubmit}>
        <div style={{ marginBottom: '15px' }}>
          <input
            id="file-input"
            type="file"
            accept=".csv,.xlsx,.xls"
            onChange={onFileChange}
            style={{ marginTop: '5px' }}
          />
        </div>
        
        <button 
          type="submit" 
          disabled={loading || !file}
          style={{ padding: '10px 20px', backgroundColor: '#007bff', color: 'white', border: 'none' }}
        >
          {loading ? 'Uploading...' : 'Upload and Distribute'}
        </button>
      </form>
    </div>
  );
};

export default UploadList;
