import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Navbar, Nav, Container } from 'react-bootstrap';
import Logo from './Logo';

const NavigationBar = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  const isActive = (path) => location.pathname === path;

  return (
    <Navbar expand="lg" className="navbar-cstech">
      <Container>
        <Navbar.Brand 
          className="navbar-brand-cstech" 
          onClick={() => navigate('/dashboard')}
          style={{ cursor: 'pointer' }}
        >
          <Logo size="md" showText={false} />
          <span className="ms-2">CSTech Portal</span>
        </Navbar.Brand>
        
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            <Nav.Link
              className={`nav-link-cstech ${isActive('/dashboard') ? 'active' : ''}`}
              onClick={() => navigate('/dashboard')}
            >
              üìä Dashboard
            </Nav.Link>
            <Nav.Link
              className={`nav-link-cstech ${isActive('/agents') ? 'active' : ''}`}
              onClick={() => navigate('/agents')}
            >
              üë• Agents
            </Nav.Link>
            <Nav.Link
              className={`nav-link-cstech ${isActive('/upload') ? 'active' : ''}`}
              onClick={() => navigate('/upload')}
            >
              üìÅ Upload
            </Nav.Link>
            <Nav.Link
              className={`nav-link-cstech ${isActive('/distributed') ? 'active' : ''}`}
              onClick={() => navigate('/distributed')}
            >
              üìã Lists
            </Nav.Link>
            <Nav.Link
              className="nav-link-cstech"
              onClick={handleLogout}
              style={{ background: 'rgba(220, 53, 69, 0.2)' }}
            >
              üö™ Logout
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default NavigationBar;
