import React from 'react';

const Logo = ({ size = 'lg', showText = true }) => {
  const sizes = {
    sm: { container: '30px', text: '16px', subtext: '10px' },
    md: { container: '40px', text: '20px', subtext: '12px' },
    lg: { container: '50px', text: '24px', subtext: '14px' }
  };

  return (
    <div className="d-flex align-items-center">
      <img 
        src="/cstech-logo.png" 
        alt="CSTech Logo" 
        style={{
          height: sizes[size].container,
          width: sizes[size].container,
          borderRadius: '8px',
          objectFit: 'contain',
          marginRight: showText ? '12px' : '0'
        }}
      />
      {showText && (
        <div className="d-flex flex-column">
          <span className="fw-bold" style={{ fontSize: sizes[size].text, color: '#1e4e7e', lineHeight: '1.1' }}>
            CSTech
          </span>
          <span style={{ fontSize: sizes[size].subtext, color: '#2c6aa0', lineHeight: '1.2' }}>
            Infosolutions
          </span>
          <span style={{ fontSize: '8px', color: '#6c757d', textTransform: 'uppercase', letterSpacing: '0.5px' }}>
            Private Limited
          </span>
        </div>
      )}
    </div>
  );
};

export default Logo;
