import React from 'react';

const Logo = ({ size = 'lg', showText = true }) => {
  const sizes = {
    sm: { container: '30px', text: '14px', subtext: '10px' },
    md: { container: '40px', text: '18px', subtext: '12px' },
    lg: { container: '50px', text: '24px', subtext: '14px' }
  };

  return (
    <div className="logo" style={styles.logo}>
      <img 
        src="/cstech-logo.png" 
        alt="CSTech Logo" 
        style={{
          ...styles.logoImage,
          height: sizes[size].container,
          width: sizes[size].container
        }}
      />
      {showText && (
        <div style={styles.logoText}>
          <div style={{...styles.mainText, fontSize: sizes[size].text}}>
            CSTech Infosolutions
          </div>
          <div style={{...styles.subText, fontSize: sizes[size].subtext}}>
            PRIVATE LIMITED
          </div>
        </div>
      )}
    </div>
  );
};

const styles = {
  logo: {
    display: 'flex',
    alignItems: 'center',
    gap: '12px',
    fontWeight: 'bold',
    color: '#2c5282', // Dark blue from your logo
    textDecoration: 'none'
  },
  logoImage: {
    borderRadius: '8px',
    objectFit: 'contain',
    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)'
  },
  logoText: {
    display: 'flex',
    flexDirection: 'column',
    lineHeight: '1.2'
  },
  mainText: {
    fontWeight: '700',
    color: '#2c5282'
  },
  subText: {
    fontWeight: '500',
    color: '#4a5568',
    opacity: '0.8'
  }
};

export default Logo;
