import React from 'react';

const Logo = ({ size = 'lg', showText = true }) => {
  const sizes = {
    sm: { container: '30px', text: '14px', subtext: '10px' },
    md: { container: '40px', text: '18px', subtext: '12px' },
    lg: { container: '50px', text: '24px', subtext: '14px' }
  };

  return (
    <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
      <img 
        src="/cstech-logo.png" 
        alt="CSTech Logo" 
        style={{
          height: sizes[size].container,
          width: sizes[size].container,
          borderRadius: '8px',
          objectFit: 'contain'
        }}
      />
      {showText && (
        <div style={{ display: 'flex', flexDirection: 'column', lineHeight: '1.2' }}>
          <span style={{ fontSize: sizes[size].text, fontWeight: 'bold', color: '#1e4e7e' }}>
            CSTech
          </span>
          <span style={{ fontSize: sizes[size].subtext, color: '#2c6aa0' }}>
            Infosolutions
          </span>
        </div>
      )}
    </div>
  );
};

export default Logo;
