import React, { useState, useEffect } from 'react';
import API from '../services/api';

const DistributedLists = () => {
  const [lists, setLists] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchLists();
  }, []);

  const fetchLists = async () => {
    try {
      const res = await API.get('/api/lists');
      setLists(res.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch distributed lists');
    }
    setLoading(false);
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>Distributed Lists</h2>
      {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>}
      
      {lists.length === 0 ? (
        <p>No distributed lists found</p>
      ) : (
        lists.map(list => (
          <div key={list._id} style={{ marginBottom: '30px', border: '1px solid #ddd', padding: '15px', borderRadius: '5px' }}>
            <h3>File: {list.originalFileName}</h3>
            <p>Distributed on: {new Date(list.distributedAt).toLocaleString()}</p>
            <p>Total items: {list.items.length}</p>
            
            <h4>Items by Agent:</h4>
            <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '10px' }}>
              <thead>
                <tr style={{ backgroundColor: '#f8f9fa' }}>
                  <th style={{ border: '1px solid #ddd', padding: '8px' }}>Agent</th>
                  <th style={{ border: '1px solid #ddd', padding: '8px' }}>First Name</th>
                  <th style={{ border: '1px solid #ddd', padding: '8px' }}>Phone</th>
                  <th style={{ border: '1px solid #ddd', padding: '8px' }}>Notes</th>
                </tr>
              </thead>
              <tbody>
                {list.items.map((item, index) => (
                  <tr key={index}>
                    <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                      {item.agentId?.name || 'Unknown Agent'}
                    </td>
                    <td style={{ border: '1px solid #ddd', padding: '8px' }}>{item.firstName}</td>
                    <td style={{ border: '1px solid #ddd', padding: '8px' }}>{item.phone}</td>
                    <td style={{ border: '1px solid #ddd', padding: '8px' }}>{item.notes || '-'}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ))
      )}
    </div>
  );
};

export default DistributedLists;
