import React from 'react';

const Logo = ({ size = 'lg', showText = true }) => {
  const sizes = {
    sm: { container: '30px', text: '14px', subtext: '10px' },
    md: { container: '40px', text: '18px', subtext: '12px' },
    lg: { container: '50px', text: '24px', subtext: '14px' }
  };

  return (
    <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
      <div style={{
        width: sizes[size].container,
        height: sizes[size].container,
        background: 'linear-gradient(135deg, #0056b3, #007bff)',
        borderRadius: '8px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: 'white',
        fontWeight: 'bold',
        fontSize: sizes[size].text,
        boxShadow: '0 2px 8px rgba(0, 86, 179, 0.3)'
      }}>
        CST
      </div>
      {showText && (
        <div style={{ display: 'flex', flexDirection: 'column', lineHeight: '1.2' }}>
          <span style={{ 
            fontSize: sizes[size].text, 
            fontWeight: 'bold', 
            color: '#0056b3',
            letterSpacing: '0.5px'
          }}>
            CSTech
          </span>
          <span style={{ 
            fontSize: sizes[size].subtext, 
            color: '#007bff',
            fontWeight: '500'
          }}>
            Infosolutions
          </span>
        </div>
      )}
    </div>
  );
};

export default Logo;
