import React, { useState } from 'react';
import API from '../services/api';

const AgentForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    mobile: '',
    password: ''
  });
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const { name, email, mobile, password } = formData;

  const onChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    try {
      await API.post('/api/agents', formData);
      setMessage('Agent created successfully');
      setFormData({ name: '', email: '', mobile: '', password: '' });
      setError('');
      setTimeout(() => window.location.reload(), 1000);
    } catch (err) {
      setError(err.response?.data?.message || 'Error creating agent');
      setMessage('');
    }
    setLoading(false);
  };

  return (
    <div className="cstech-card">
      <div className="card-header">
        <h3>Create New Agent</h3>
      </div>
      <div className="card-body">
        {message && <div style={{color: 'green', marginBottom: '15px', padding: '10px', background: '#f0fff0', borderRadius: '4px'}}>{message}</div>}
        {error && <div style={{color: '#D10000', marginBottom: '15px', padding: '10px', background: '#fff0f0', borderRadius: '4px'}}>{error}</div>}
        
        <form onSubmit={onSubmit}>
          <div className="form-group">
            <label className="form-label">Full Name</label>
            <input
              type="text"
              name="name"
              value={name}
              onChange={onChange}
              required
              className="form-control"
              placeholder="Enter agent's full name"
            />
          </div>
          
          <div className="form-group">
            <label className="form-label">Email Address</label>
            <input
              type="email"
              name="email"
              value={email}
              onChange={onChange}
              required
              className="form-control"
              placeholder="Enter agent's email"
            />
          </div>
          
          <div className="form-group">
            <label className="form-label">Mobile Number</label>
            <input
              type="text"
              name="mobile"
              value={mobile}
              onChange={onChange}
              required
              className="form-control"
              placeholder="+91 1234567890"
            />
          </div>
          
          <div className="form-group">
            <label className="form-label">Password</label>
            <input
              type="password"
              name="password"
              value={password}
              onChange={onChange}
              required
              className="form-control"
              placeholder="Set a password for the agent"
            />
          </div>
          
          <button 
            type="submit" 
            disabled={loading}
            className="btn btn-primary"
            style={{width: '100%'}}
          >
            {loading ? 'Creating Agent...' : 'Create Agent'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default AgentForm;
